  const { themeName } = useTheme();

  const handleClick = e => {
    e.preventDefault();
    if (themeName === 'day') {
      setThemeName('night');
    } else {
      setLogin('day');
    }
  }

  return (
    <div className={`light-switch ${themeName}`}>
      <div className="on" onClick="">DAY</div>
      <div className="off" onClick="">NIGHT</div>
    </div>
  );
}

export default LightSwitch;
